import { useRef, useEffect, useState } from "react";
import { Formik, Field, Form, useFormik } from "formik"
import * as yup from "yup"
import { stringify, v4 as uuidv4 } from 'uuid';

function AuthForm({ setAuthForm }) {
    const inputRef = useRef();
    const closeModal = () => {
        setAuthForm(false)
    };

    useEffect(() => {
        inputRef.current.focus();
    }, [])

    const handleKeyDown = (e) => {
        if (e.key === 'Escape') {
            closeModal();
        }
    };
    useEffect(() => {
        document.addEventListener('keydown', handleKeyDown);
        return () => {
            document.removeEventListener('keydown', handleKeyDown);
        };
    }, []);



    const validationSchema = yup.object().shape({
        title: yup
            .string()
            .required('제목을 입력해 주세요.')
            .max(100, '100자 이내로 작성해 주세요.'),
        time: yup
            .string()
            .required('시간을 선택해 주세요.'),
        description: yup
            .string()
            .max(300, '300자 이내로 작성해 주세요.'),
        weekCheck: yup
            .object()
            .shape({
                "월": yup.boolean(),
                "화": yup.boolean(),
                "수": yup.boolean(),
                "목": yup.boolean(),
                "금": yup.boolean(),
                "토": yup.boolean(),
                "일": yup.boolean(),
            })
            .test(
                'isWeekChecked',
                '요일을 선택해 주세요.',
                obj => Object.values(obj).some(checked => checked === true)
            )
    })

    const formik = useFormik({
        initialValues: {
            title: '',
            time: '',
            description: '',
            weekCheck: {
                '월': false,
                '화': false,
                '수': false,
                '목': false,
                '금': false,
                '토': false,
                '일': false
            }
        },
        onSubmit: async (values) => {
            try {
                await fetch(`${process.env.REACT_APP_SERVER_URL}/routine`, {
                    method: 'POST',
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify(values)
                });

                setTimeout(async () => {
                    const res = await fetch(`${process.env.REACT_APP_SERVER_URL}/routine`);
                    const data = await res.json();
                    data.sort((a, b) => new Date("1970/01/01 " + a.time) - new Date("1970/01/01 " + b.time));


                }, 1);

            } catch (error) {
                console.error(error);
            }
            closeModal();
        },
        validationSchema
    })



    return (
        <div>
            <div className="fixed w-full h-full z-10">
                <div className="w-full h-full bg-black opacity-80"
                    onMouseDown={() => {
                        setAuthForm(false)
                    }}></div>

                <div className="absolute top-1/2 left-1/2 -translate-y-1/2 -translate-x-1/2 ">
                    <button className="float-right m-1 mr-3 hover:scale-110"
                        onMouseDown={() => {
                            setAuthForm(false)
                        }}>x</button>
                    <form onSubmit={formik.handleSubmit}>
                        <div className="rounded-lg bg-gray-100 p-6">
                            <div>

                                <input className="p-1 mr-1 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md focus:ring-1"
                                    ref={inputRef}
                                    maxLength="10"
                                    placeholder="아이디"
                                    type="text"
                                    name="id"
                                    value={formik.values.id}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                />
                                {formik.touched.id && formik.errors.id && <p className="text-sm text-red-400 ml-2">{formik.errors.id}</p>}
                            </div>
                            <div>

                                <input className="mt-7 p-1 mr-1 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md focus:ring-1"
                                    maxLength="20"
                                    placeholder="비밀번호"
                                    type="text"
                                    name="pw"
                                    value={formik.values.pw}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                />
                                {/* {formik.touched.pw && formik.errors.pw && <p>{formik.errors.pw}</p>} */}
                            </div>





                            {formik.isSubmitting ? (
                                <button
                                    type="submit"
                                    className="block cursor-pointer w-full py-1.5 mt-3 text-white font-medium text-sm rounded shadow-md bg-blue-500"
                                >
                                    ...
                                </button>
                            ) : (
                                <button
                                    type="submit"
                                    className="block cursor-pointer w-full py-1.5 mt-3 text-white font-medium text-sm rounded shadow-md bg-blue-600 hover:bg-blue-700  focus:bg-blue-700 focus:shadow-lg focus:outline-none focus:ring-0 active:bg-blue-800 active:shadow-lg"
                                    onClick={() => {
                                        formik.setFieldValue('action', 'delete');
                                    }}
                                >
                                    로그인
                                </button>
                            )}

                            {formik.isSubmitting ? (
                                <button
                                    type="submit"
                                    className="block cursor-pointer w-full py-1.5 mt-3 text-white font-medium text-sm rounded shadow-md bg-teal-500"
                                >
                                    ...
                                </button>
                            ) : (
                                <button
                                    type="submit"
                                    className="block cursor-pointer w-full py-1.5 mt-3 text-white font-medium text-sm rounded shadow-md bg-teal-600 hover:bg-teal-700  focus:bg-teal-700 focus:shadow-lg focus:outline-none focus:ring-0 active:bg-teal-800 active:shadow-lg"
                                    onClick={() => {
                                        formik.setFieldValue('action', 'update');
                                    }}
                                >
                                    회원가입
                                </button>
                            )}

                            {formik.submitCount > 0 && formik.errors && (
                                console.error(Object.values(formik.errors).join('\n'))
                            )}


                        </div>
                    </form>
                </div>
            </div>
        </div >
    )
}

export default AuthForm;
