import { Link } from "react-router-dom";
import { useState } from 'react';
import AuthForm from '../components/AuthForm';



function Header({ isSignIn, setIsSignIn }) {

    const [authForm, setAuthForm] = useState(false)


    return (
        <>
            {
                authForm === true && <AuthForm setAuthForm={setAuthForm} setIsSignIn={setIsSignIn} />
            }


            <header className="text-gray-600 body-font shadow-xl">
                <div className="container mx-auto flex flex-wrap p-5 flex-col md:flex-row items-center">
                    <div className="flex title-font font-medium items-center text-gray-900 mb-4 md:mb-0">
                        <Link to="/" className="ml-3 text-xl hover:cursor-pointer">Tailblocks</Link>
                    </div>
                    <nav className="md:ml-auto flex flex-wrap items-center text-base justify-center">
                        <Link to='/service' className="mr-5 hover:text-gray-900 cursor-pointer">Routines</Link>
                        <Link to='/posts' className="mr-5 hover:text-gray-900 cursor-pointer">Posts</Link>
                        {isSignIn ? (
                            <div className="mr-5 hover:text-gray-900 cursor-pointer"
                                onClick={async () => {
                                    try {
                                        const res = await fetch(`${process.env.REACT_APP_SERVER_URL}/user/signup`, {
                                            method: 'POST',
                                            headers: {
                                                'Content-Type': 'application/json'
                                            },

                                            credentials: 'include'
                                        })
                                        if (res.ok) {
                                            setIsSignIn(true);
                                            setAuthForm(false);
                                        }
                                    }
                                    catch (error) {
                                        console.error(error);
                                    }
                                    setIsSignIn(false)
                                }}>Sign out</div>
                        ) : (
                            <div className="mr-5 hover:text-gray-900 cursor-pointer"
                                onClick={() => {
                                    setAuthForm(true)
                                }}
                            >Sign in</div>)

                        }

                    </nav>
                </div>
            </header>
        </>
    )
}

export default Header;
